import {
    FIRST_NAME_CHANGED,
    LAST_NAME_CHANGED,
    FULL_NAME_CHANGED,
    RECEIVE_DATA,
} from '../actions/types';

let convert = require('xml-js');

const INITIAL_STATE = { firstName: '', lastName: '', data: [] };

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case FIRST_NAME_CHANGED:
            return { ...state, firstName: action.payload };
        case LAST_NAME_CHANGED:
            console.log(action.payload)
            return { ...state, lastName: action.payload };
        case FULL_NAME_CHANGED:
            let text = action.payload || '';
            let [firstName, lastName] = text.split(' ');
            return { ...state, firstName: firstName ? firstName : '', lastName: lastName ? lastName : '' };
        case RECEIVE_DATA:
            let tmp = '<response>' +
                '<returncode>SUCCESS</returncode>' +
                '<version>2.0</version>' +
                '<servers>' +
                ' <server>' +
                '<serverID>211a1b81-cf0f-437f-b2fc-346d407796c6</serverID>' +
                '<serverUrl>https://vcs4.etfbl.net/bigbluebutton/api/</serverUrl>' +
                ' <online>true</online>' +
                ' <loadMultiplier>1.0</loadMultiplier>' +
                '<enabled>true</enabled>' +
                '<load>0.0</load>' +
                ' </server>' +
                ' </servers>' +
                '</response>';
            let xml =
                '<?xml version="1.0" encoding="utf-8"?>' +
                '<note importance="high" logged="true">' +
                '    <title>Happy</title>' +
                '    <todo>Work</todo>' +
                '    <todo>Play</todo>' +
                '</note>';
            let result1 = convert.xml2json(xml, { compact: true, spaces: 4 });
            let result2 = convert.xml2json(xml, { compact: false, spaces: 4 });
            console.log(result1, result2);
            return { ...state, data: action.payload }
        default:
            return state;
    }
};
